mapper.select:
select

  distinct

'false' as QUERYID,

from LoanExtension

  order by ${orderByClause}


  ${pageCondition}

select 

from LoanExtension
where LoanNo = #{loanNo,jdbcType=NVARCHAR}
  and ProjectId = #{projectId,jdbcType=INTEGER}

mapper.delete:
delete from LoanExtension
where LoanNo = #{loanNo,jdbcType=NVARCHAR}
  and ProjectId = #{projectId,jdbcType=INTEGER}
